const Cards = (props)=>{
    const {title,subhead,btn,desc,image,link} = props.data;
    return (
        <Card style={{margin:30}}>
        <Card.Title title={title} subtitle={subhead} left={LeftContent} />
        <Card.Content>
          <Title>{title}</Title>
          <Paragraph>{desc}</Paragraph>
        </Card.Content>
        <Card.Cover source={{ uri:  image}} />
        <Card.Actions>
          <Button onPress={()=>{
             Linking.openURL(`${link}`);
          }}>{btn}</Button>
        </Card.Actions>
      </Card>
    )
}

